    setTimeout(2000);
  }

  const getVisitor = () => {

    Axios.get('http://localhost:3001/getVisitorMoment').then((response) => {setVisitorMoment(JSON.parse(JSON.stringify(response.data))[0].visitorMoment);});
    setTimeout(2000);
    Axios.get('http://localhost:3001/getVisitorDaily').then((response) => {setVisitorDaily(response.data[0].visitorDaily);});
    setTimeout(2000);
    Axios.get('http://localhost:3001/getVisitorTotal').then((response) => {setVisitorTotal(response.data[0].visitorTotal);});
  }

  const getVisitorDaily = () => {
    Axios.get('http://localhost:3001/getVisitorDaily').then((response) =>
    { setVisitorDaily(response.data);});
  }

  const getVisitorTotal = () => {
    Axios.get('http://localhost:3001/getVisitorTotal').then((response) =>
    { setVisitorTotal(response.data);});
  }

