import './App.css';
import React, { Component } from 'react';
import { Box, Button, FormControl, TextField} from '@material-ui/core';
import { connect } from 'react-redux';
import {addMoment,addDaily,addTotal,resetMoment,resetDaily,resetTotal,subMoment} from './action';
import { AsyncStorage } from '@react-native-async-storage';

class App extends Component { 

  constructor(props){
    super(props);
    this.state = { errorOpen: false, isLoading: false, customer: {}}

    this.handleChangeGroupNumber = this.handleChangeGroupNumber.bind(this);
    this.handleChangePublisherNumber = this.handleChangePublisherNumber.bind(this);
    this.handleChangeTitleNumber = this.handleChangeTitleNumber.bind(this); 

    this.numberMoment = 0;
    this.numberDaily = 0;
    this.numberTotal = 0;
  }
  state = {
    momentVisitor: 0,
    dailyVisitor: 0,
    totalVisitor: 0
  }
  handleChangeGroupNumber(event) {
    this.setState({groupNumber: event.target.value});}
  handleChangePublisherNumber(event){
    this.setState({publisherNumber: event.target.value});}
  handleChangeTitleNumber(event){
    this.setState({titleNumber: event.target.value});
  }

  /*addVisitor = async () => {
    this.props.addMoment();
    this.props.addDaily();
    this.props.addTotal();
  }

  subVisitor = async () => {
    this.props.subMoment();
  }

  resMoment = async () => {
    this.props.resetMoment();
  }

  resDaily = async () => {
    this.props.resetDaily();
  }

  resTotal = async () => {
    this.props.resetTotal();
  } */

  addVisitor = async () => {
    try {
      await AsyncStorage.setItem('momentVisitor', this.state.momentVisitor + 1);
      await AsyncStorage.setItem('dailyVisitor', this.state.dailyVisitor + 1);
      await AsyncStorage.setItem('totalVisitor', this.state.totalVisitor + 1);
    } catch (error) {
      // Error saving data
    }
  };

  subVisitor = async () => {
    try {
      await AsyncStorage.setItem('momentVisitor', this.state.momentVisitor - 1);
    } catch (error) {
      // Error saving data
    }
  };

  resMoment = async () => {
    try {
      await AsyncStorage.setItem('momentVisitor', 0);
    } catch (error) {
      // Error saving data
    }
  };

  resDaily = async () => {
    try {
      await AsyncStorage.setItem('dailyVisitor', 0);
    } catch (error) {
      // Error saving data
    }
  };

  resTotal = async () => {
    try {
      await AsyncStorage.setItem('dailyTotal', 0);
    } catch (error) {
      // Error saving data
    }
  };

  getVisitorMoment = async () => {
    try {
      const constMoment = await AsyncStorage.getItem('momentVisitor');
      const constDaily = await AsyncStorage.getItem('dailyVisitor');
      const constTotal = await AsyncStorage.getItem('totalVisitor');

      if (constTota!==null){
          this.setState({momentVisitor: constMoment});
          this.setState({dailyVisitor: constDaily});
          this.setState({totalVisitor: constTotal});
      }

    } catch (error) {
      // Error retrieving data
    }
  };

  render(){
  return (
    <div className="App">
      <header className="App-header">
        <Box className="App-Header">
          <h2 className="App-Dialog-Moment">Aktuelle Besucher</h2>
          <div style={{ height: 300, width: '90%' }}>
            <Button variant="outlined" color="primary" className ="App-Moment-Add-Button" onClick={this.addVisitor}>
                    +
                  </Button>
            <TextField
                            className ="App-Output-Moment" 
                            disabled={false}
                            label=""
                            type={Number}
                            value={this.state.momentVisitor}
                        />
            <Button variant="outlined" color="primary" className ="App-Moment-Sub-Button" onClick={this.subVisitor}>
                    -
            </Button>
            <Button variant="outlined" color="primary" className ="App-Moment-Reset-Button" onClick={this.resMoment}>
                    Reset
            </Button>
          </div>
          <h2 className="App-Dialog-Daily">Heutige Besucher</h2>
          <div style={{ height: 300, width: '90%' }}>
            <TextField
                            className ="App-Output-Daily" 
                            disabled={false}
                            label=""
                            type={Number}
                            value={this.state.dailyVisitor}
                        />
            <Button variant="outlined" color="primary" className ="App-Daily-Reset-Button" onClick={this.resDaily}>
                    Reset
            </Button>
          </div>
          <h2 className="App-Dialog-Total">Gesamte Besucher</h2>
          <div style={{ height: 300, width: '90%' }}>
            <TextField
                            className ="App-Output-Total" 
                            disabled={false}
                            label=""
                            type={Number}
                            value={this.state.totalVisitor}
                        />
            <Button variant="outlined" color="primary" className ="App-Daily-Total-Button" onClick={this.resTotal}>
                    Reset
            </Button>
          </div>
        </Box>
      </header>
    </div>
  );
  }
}

